                      onChange={(e) => setI_5(e.target.value)}
                      required
                    />
                    <h1 className="label-caption">Category :</h1>
                    <select
                      required
                      value={cat1}
                      name="category"
                      className="category"
                      onChange={(e) => setCat1(e.target.value)}
                    >
                      <option value="" disabled selected>
                        Select category
                      </option>
                      <option value="food">Food</option>
                      <option value="health">Health & Fitness</option>
                      <option value="travel">Travel</option>
                      <option value="movie">Movie</option>
                      <option value="education">Education</option>
                    </select>
                  </div>
                )}
              </>
            )}
            {addStoryIndex === 6 && (
              <>
                {innerWidth > 500 && (
                  <table>
                    <tbody>
                      <tr>
                        <td className="label-caption">Heading :</td>
                        <td>
                          <input
                            type="text"
                            name="s6_h"
                            className="heading"
                            placeholder="Your heading"
                            value={h_6}
                            onChange={(e) => setH_6(e.target.value)}
                            required
                          />
                        </td>
                      </tr>
                      <tr>
                        <td className="label-caption">Description :</td>
                        <td>
                          <textarea
                            name="s6_d"
                            className="desc"
                            placeholder="Story Description"
                            value={d_6}
                            onChange={(e) => setD_6(e.target.value)}
                            required
                          ></textarea>
                        </td>
                      </tr>
                      <tr>
                        <td className="label-caption">Image :</td>
                        <td>
                          <input
                            type="text"
                            name="s6_i"
                            className="image-url"
                            placeholder="Add Image url"
                            value={i_6}
                            onChange={(e) => setI_6(e.target.value)}
                            required
                          />
                        </td>
                      </tr>
                      <tr>
                        <td className="label-caption">Category :</td>
                        <td>
                          <select
                            name="category"
                            className="category"
                            onChange={(e) => setCat1(e.target.value)}
                            value={cat1}
                          >
                            <option value="" disabled selected>
                              Select category
                            </option>
                            <option value="food">Food</option>
                            <option value="health">Health & Fitness</option>
                            <option value="travel">Travel</option>
                            <option value="movie">Health & Fitness</option>
                            <option value="education">Education</option>
                          </select>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                )}
                {innerWidth < 500 && (
                  <div className="mb-form-element">
                    <h1 className="label-caption">Heading :</h1>
                    <input
                      type="text"
                      name="s6_h"
                      className="heading"
                      placeholder="Your heading"
                      value={h_6}
                      onChange={(e) => setH_6(e.target.value)}
                      required
                    />
                    <h1 className="label-caption">Description :</h1>
                    <textarea
                      name="s6_d"
                      className="desc"
                      placeholder="Story Description"
                      value={d_6}
                      onChange={(e) => setD_6(e.target.value)}
                      required
                    ></textarea>
                    <h1 className="label-caption">Image :</h1>
                    <input
                      type="text"
                      name="s6_i"
                      className="image-url"
                      placeholder="Add Image url"
                      value={i_6}
                      onChange={(e) => setI_6(e.target.value)}
                      required
                    />
                    <h1 className="label-caption">Category :</h1>
                    <select
                      required
                      value={cat1}
                      name="category"
                      className="category"
                      onChange={(e) => setCat1(e.target.value)}
                    >
                      <option value="" disabled selected>
                        Select category
                      </option>
                      <option value="food">Food</option>
                      <option value="health">Health & Fitness</option>
                      <option value="travel">Travel</option>
                      <option value="movie">Movie</option>
                      <option value="education">Education</option>
                    </select>
                  </div>
                )}
              </>
            )}
            <div className="buttons-navigation-submit">
              <div className="navigation-btn">
                {innerWidth > 500 && (
                  <div
                    className="previous-btn"
                    onClick={() => onClickPreviousSlide()}
                  >
                    Previous
                  </div>
                )}
                {innerWidth > 500 && (
                  <div
                    className="add-next-btn"
                    onClick={() => onClickNextSlide()}
                  >
                    Next
                  </div>
                )}
              </div>
              <div className="submit-btn">
                <button type="submit" className="submit-btn">
                  Post
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
